create database spark;

USE spark;

DROP TABLE `person`;

CREATE TABLE `person` (
  `id` mediumint(8) unsigned NOT NULL auto_increment,
  `name` varchar(255) default NULL,
  `dob` varchar(255),
  `email` varchar(255) default NULL,
  PRIMARY KEY (`id`)
) AUTO_INCREMENT=1;



DROP TABLE `encoding`;

CREATE TABLE `encoding` (
  `id` mediumint(8) unsigned NOT NULL auto_increment,
  `city` varchar(255),
  `travel_date` varchar(255),
  `name` varchar(255) default NULL,
  PRIMARY KEY (`id`)
) AUTO_INCREMENT=1;

